gregorianToHebrewConverter.convertButtonListener:
document.getElementById('convert-btn').addEventListener('click', async function() {
    const gregorianDate = document.getElementById('gregorian-date').value;

    // Call the Hebcal API to convert Gregorian date to Hebrew date
    const response = await fetch(`https://www.hebcal.com/converter?cfg=json&gy=${gregorianDate.substr(0, 4)}&gm=${gregorianDate.substr(5, 2)}&gd=${gregorianDate.substr(8, 2)}`);
    const data = await response.json();

    // Display the converted Hebrew date
    document.getElementById('hebrew-date').textContent = data.hebrew;
});

currentDate.renderCalendarFunction:
const renderCalendar = () => {
    let firstDayOfMonth = new Date(currentYear,currentMonth,1 ).getDay(),
    lastDateOfMonth = new Date(currentYear,currentMonth +1, 0 ).getDate(),
    lastDayOfMonth = new Date(currentYear,currentMonth, lastDateOfMonth).getDay(),
    lastDateOfLastMonth = new Date(currentYear,currentMonth, 0 ).getDate();
     let liTag ="";

     for ( let i = firstDayOfMonth; i > 0;  i--) {
        liTag +=`<li class="inactive"> ${lastDateOfLastMonth - i + 1} </li>`;
     };

     for ( let i = 1; i <= lastDateOfMonth;  i++) {
        let isToday = i === date.getDate() && currentMonth=== new Date().getMonth() && currentYear === new Date().getFullYear() ? "active":"";
        liTag +=`<li class="${isToday}">${i}</li>`;
     };

     for ( let i = lastDayOfMonth; i < 6;  i++) {
        liTag +=`<li class="inactive"> ${ i - lastDayOfMonth  + 1} </li>`;
     };
     currentDateElement.innerText = `${monthsArray[currentMonth]} ${currentYear}`;
     daysTagElement.innerHTML = liTag;
}
renderCalendar()

